import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";
import "./EmergencyCases.css";

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

function formatDate(dateStr) {
  if (!dateStr) return "";
  const d = new Date(dateStr);
  return d.toLocaleDateString("en-IN", {
    day: "numeric",
    month: "short",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  });
}

function EmergencyCases() {
  const [data, setData] = useState([]);
  const [nurseName, setNurseName] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Voice states
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [triageResult, setTriageResult] = useState(null);
  const [triageLoading, setTriageLoading] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [translating, setTranslating] = useState(false);
  const [language, setLanguage] = useState("auto");

  useEffect(() => {
    fetch("http://localhost:8000/api/user-role/", {
      credentials: "include",
    })
      .then((res) => {
        if (!res.ok) {
          navigate("/");
          return null;
        }
        return res.json();
      })
      .then((roleData) => {
        if (!roleData) return;
        if (roleData.role !== "nurse") {
          navigate("/");
          return;
        }
        setNurseName(roleData.name || "");
        if (roleData.language) setLanguage(roleData.language);
      });
  }, [navigate]);

  useEffect(() => {
    fetch("http://localhost:8000/api/nurse-dashboard/", {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((result) => {
        if (Array.isArray(result)) {
          const emergency = result.filter((item) => {
            const risk = (item.predicted_risk || "").toLowerCase();
            return risk === "high" || risk === "critical";
          });
          setData(emergency);
        }
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleLogout = async () => {
    const csrfToken = getCookie("csrftoken");
    await fetch("http://localhost:8000/api/logout/", {
      method: "POST",
      credentials: "include",
      headers: { "X-CSRFToken": csrfToken },
    });
    navigate("/");
  };

  // ðŸ”¥ Start Recording
  const startListening = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

      const recorder = new MediaRecorder(stream);
      setMediaRecorder(recorder);

      const chunks = [];

      recorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      recorder.onstop = async () => {
        const audioBlob = new Blob(chunks, { type: "audio/webm" });

        const formData = new FormData();
        formData.append("audio", audioBlob, "recording.webm");
        formData.append("language", language);

        setTranslating(true);
        try {
          const response = await fetch("http://localhost:8000/api/whisper/", {
            method: "POST",
            body: formData,
          });

          const data = await response.json();

          if (data.translated_text) {
            setTranscript(data.translated_text);
          } else {
            alert("Transcription failed.");
          }
        } catch {
          alert("Error sending audio to server.");
        }
        setTranslating(false);
      };

      recorder.start();
      setIsListening(true);
    } catch {
      alert("Microphone access denied.");
    }
  };

  // ðŸ”¥ Stop Recording
  const stopListening = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      setIsListening(false);
    }
  };

  const handleTriageSubmit = async () => {
    if (!transcript.trim()) return;

    setTriageLoading(true);
    setTriageResult(null);

    try {
      const csrfToken = getCookie("csrftoken");

      const res = await fetch("http://localhost:8000/api/triage/", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          "X-CSRFToken": csrfToken,
        },
        body: JSON.stringify({ symptoms: transcript }),
      });

      const result = await res.json();
      setTriageResult(result);
    } catch {
      setTriageResult({ error: "Failed to reach server." });
    }

    setTriageLoading(false);
  };

  const clearVoice = () => {
    setTranscript("");
    setTriageResult(null);
  };

  if (loading) return <div className="nurse-loading">Loading...</div>;

  return (
    <div className="nurse-page">
      <header className="nurse-header">
        <div className="nurse-header-left">
          <span>Apex Health</span>
        </div>
        <div className="nurse-header-right">
          <span className="nurse-name">
            Nurse: <strong>{nurseName}</strong>
          </span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <main className="nurse-main">
        <h2 className="section-title" style={{ color: "#f87171" }}>
          Voice Triage (Whisper)
        </h2>

        <div className="language-picker">
          <label>Patient Language</label>
          <select value={language} onChange={e => setLanguage(e.target.value)} disabled={isListening}>
            <option value="auto">Auto-detect</option>
            <option value="ta">Tamil</option>
            <option value="hi">Hindi</option>
            <option value="te">Telugu</option>
            <option value="kn">Kannada</option>
            <option value="ml">Malayalam</option>
            <option value="mr">Marathi</option>
            <option value="bn">Bengali</option>
            <option value="gu">Gujarati</option>
            <option value="ur">Urdu</option>
            <option value="pa">Punjabi</option>
            <option value="en">English</option>
            <option value="es">Spanish</option>
            <option value="fr">French</option>
            <option value="ar">Arabic</option>
            <option value="zh">Chinese</option>
            <option value="ja">Japanese</option>
            <option value="ko">Korean</option>
          </select>
        </div>

        <div className="voice-controls">
          {isListening ? (
            <button
              className="voice-btn voice-btn-stop"
              onClick={stopListening}
            >
              Stop Recording
            </button>
          ) : (
            <button
              className="voice-btn voice-btn-start"
              onClick={startListening}
            >
              Start Recording
            </button>
          )}

          {transcript && (
            <button className="voice-btn voice-btn-clear" onClick={clearVoice}>
              Clear
            </button>
          )}
        </div>

        {isListening && <div className="voice-indicator">Listening...</div>}

        {translating && (
          <div className="voice-translating">
            <div className="translating-spinner"></div>
            <span>Translating audio...</span>
          </div>
        )}

        {transcript && (
          <div className="voice-transcript">
            <label>Translated Text:</label>
            <textarea
              value={transcript}
              onChange={(e) => setTranscript(e.target.value)}
              rows={3}
            />
            <button
              className="voice-btn voice-btn-submit"
              onClick={handleTriageSubmit}
              disabled={triageLoading}
            >
              {triageLoading ? "Analyzing..." : "Analyze Symptoms"}
            </button>
          </div>
        )}

        {triageResult && !triageResult.error && (
          <div className="triage-result">
            <div>
              <strong>Department:</strong> {triageResult.assigned_department}
            </div>
            <div>
              <strong>Confidence:</strong>{" "}
              {Math.round(triageResult.confidence * 100)}%
            </div>
          </div>
        )}

        {triageResult && triageResult.error && (
          <p className="voice-error">{triageResult.error}</p>
        )}
      </main>
    </div>
  );
}

export default EmergencyCases;
