import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";
import "./EmergencyCases.css";

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

const DEPARTMENTS = [
  "Emergency",
  "General_Medicine",
  "Cardiology",
  "Neurology",
  "Pulmonology",
  "Gastroenterology",
  "Orthopedics",
  "Pediatrics",
  "Nephrology",
  "Endocrinology",
];

const EMPTY_PATIENT = {
  full_name: "",
  age: "",
  gender: "",
  blood_group: "",
  allergies: "",
  past_surgeries: "",
  diabetes: false,
  hypertension: false,
  heart_disease: false,
  asthma: false,
  chronic_kidney_disease: false,
  previous_stroke: false,
  smoker: false,
  obese: false,
  previous_heart_attack: false,
  previous_hospitalization: false,
};

function EmergencyCases() {
  const navigate = useNavigate();
  const [nurseName, setNurseName] = useState("");

  const [patientType, setPatientType] = useState("existing");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [newPatient, setNewPatient] = useState({ ...EMPTY_PATIENT });

  const [inputMode, setInputMode] = useState(null);
  const [transcript, setTranscript] = useState("");

  const [triageResult, setTriageResult] = useState(null);
  const [triageLoading, setTriageLoading] = useState(false);

  const [chosenDepartment, setChosenDepartment] = useState("");
  const [doctors, setDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState(null);

  const [confirming, setConfirming] = useState(false);
  const [confirmed, setConfirmed] = useState(null);

  const csrfToken = getCookie("csrftoken");

  // ðŸ”¹ Load nurse
  useEffect(() => {
    fetch("http://localhost:8000/api/user-role/", { credentials: "include" })
      .then((res) => res.json())
      .then((data) => {
        if (data.role !== "nurse") navigate("/");
        else setNurseName(data.name);
      });
  }, [navigate]);

  // ðŸ”¹ Patient search
  useEffect(() => {
    if (patientType !== "existing" || searchQuery.length < 2) {
      setSearchResults([]);
      return;
    }

    const timer = setTimeout(() => {
      fetch(`http://localhost:8000/api/patient/search/?q=${searchQuery}`, {
        credentials: "include",
      })
        .then((res) => res.json())
        .then((data) => setSearchResults(data));
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery, patientType]);

  // ðŸ”¹ Triage submit
  const handleTriageSubmit = async () => {
    if (!transcript.trim()) return;

    setTriageLoading(true);
    setTriageResult(null);

    const res = await fetch("http://localhost:8000/api/triage/", {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
        "X-CSRFToken": csrfToken,
      },
      body: JSON.stringify({ symptoms: transcript }),
    });

    const data = await res.json();
    setTriageResult(data);
    setTriageLoading(false);

    if (data.assigned_department) {
      setChosenDepartment(data.assigned_department);
      setSelectedDoctor(null);
      setDoctors([]);
    }
  };

  // ðŸ”¹ Fetch doctors when department changes
  useEffect(() => {
    if (!chosenDepartment) return;

    fetch(`http://localhost:8000/api/doctors/?department=${chosenDepartment}`, {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((data) => {
        setDoctors(data);
        if (data.length > 0) setSelectedDoctor(data[0]); // auto least loaded
      });
  }, [chosenDepartment]);

  // ðŸ”¹ Confirm Emergency
  const handleConfirm = async () => {
    if (!selectedDoctor || !selectedPatient) return;

    setConfirming(true);

    const res = await fetch("http://localhost:8000/api/emergency-confirm/", {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
        "X-CSRFToken": csrfToken,
      },
      body: JSON.stringify({
        patient_id: selectedPatient.id,
        doctor_id: selectedDoctor.id,
        department: chosenDepartment,
      }),
    });

    const data = await res.json();

    if (res.ok) setConfirmed(data);
    else alert(data.error || "Failed to confirm");

    setConfirming(false);
  };

  // ðŸ”¹ Reset
  const resetAll = () => {
    setTranscript("");
    setTriageResult(null);
    setChosenDepartment("");
    setDoctors([]);
    setSelectedDoctor(null);
    setConfirmed(null);
    setInputMode(null);
    setSelectedPatient(null);
  };

  return (
    <div className="nurse-page">
      <header className="nurse-header">
        <span>Apex Health</span>
        <span>Nurse: {nurseName}</span>
      </header>

      <main className="nurse-main">
        {/* PATIENT */}
        {!selectedPatient && (
          <>
            <h2>Select Patient</h2>
            <input
              placeholder="Search patient..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            {searchResults.map((p) => (
              <div key={p.id} onClick={() => setSelectedPatient(p)}>
                {p.full_name} ({p.age})
              </div>
            ))}
          </>
        )}

        {selectedPatient && !confirmed && (
          <>
            <h3>{selectedPatient.full_name}</h3>

            {/* Input */}
            {!inputMode && (
              <>
                <button onClick={() => setInputMode("manual")}>
                  Manual Input
                </button>
                <button onClick={() => setInputMode("voice")}>
                  Voice Input
                </button>
              </>
            )}

            {inputMode === "manual" && (
              <>
                <textarea
                  value={transcript}
                  onChange={(e) => setTranscript(e.target.value)}
                  placeholder="Enter symptoms..."
                />
                <button onClick={handleTriageSubmit} disabled={triageLoading}>
                  {triageLoading ? "Analyzing..." : "Analyze"}
                </button>
              </>
            )}

            {/* RESULT */}
            {triageResult && (
              <>
                <h4>Department: {chosenDepartment.replace(/_/g, " ")}</h4>
                <p>
                  Confidence:{" "}
                  {triageResult.confidence
                    ? Math.round(triageResult.confidence * 100)
                    : 0}
                  %
                </p>

                <h4>Select Doctor</h4>
                {doctors.map((doc) => (
                  <div
                    key={doc.id}
                    onClick={() => setSelectedDoctor(doc)}
                    style={{
                      background:
                        selectedDoctor?.id === doc.id ? "#ddd" : "transparent",
                    }}
                  >
                    {doc.name} ({doc.patient_count} patients)
                  </div>
                ))}

                {selectedDoctor && (
                  <button onClick={handleConfirm} disabled={confirming}>
                    {confirming ? "Confirming..." : "Confirm Emergency"}
                  </button>
                )}
              </>
            )}
          </>
        )}

        {confirmed && (
          <>
            <h3>Emergency Confirmed</h3>
            <p>
              {confirmed.patient} â†’ Dr. {confirmed.doctor} (
              {confirmed.department.replace(/_/g, " ")})
            </p>
            <button onClick={resetAll}>New Case</button>
          </>
        )}
      </main>
    </div>
  );
}

export default EmergencyCases;
