import "./DnaAnimation.css";

const RUNG_COUNT = 50;
const TWIRLS = 2.5;
const ANIM_DURATION = 8; // seconds per full oscillation cycle

// Pre-compute particles (stable across re-renders)
const PARTICLES = Array.from({ length: 25 }, (_, i) => {
  const seed = (i * 7 + 13) % 100;
  return {
    size: 1.5 + (seed / 100) * 5,
    left: 3 + ((seed * 3 + i * 17) % 94),
    top: (seed * 7 + i * 11) % 100,
    duration: 5 + (seed / 100) * 9,
    delay: (i * 3) % 8,
    isBlue: i % 3 !== 0,
    isDrift: i % 2 === 0,
  };
});

const RAYS = [
  { w: 180, h: 80, left: "15%", top: "20%", color: "rgba(0,180,216,0.12)", dur: 5, delay: 0 },
  { w: 120, h: 60, left: "60%", top: "65%", color: "rgba(139,92,246,0.1)", dur: 6, delay: 1.5 },
  { w: 200, h: 50, left: "30%", top: "80%", color: "rgba(67,97,238,0.08)", dur: 7, delay: 3 },
];

function DnaAnimation() {
  // Each rung gets a staggered delay so that across all rungs,
  // the phase spans TWIRLS full cycles — creating the visible helix shape
  const rungs = Array.from({ length: RUNG_COUNT }, (_, i) => {
    const topPct = (i / (RUNG_COUNT - 1)) * 100;
    // Delay offset: spread TWIRLS full cycles across all rungs
    const delay = -(i / RUNG_COUNT) * TWIRLS * ANIM_DURATION;

    return (
      <div
        key={i}
        className="dna-rung"
        style={{ top: `${topPct}%` }}
      >
        {/* Sphere A — cyan strand */}
        <div
          className="dna-sphere dna-sphere-a"
          style={{ animationDelay: `${delay}s`, left: "50%", marginLeft: -9 }}
        />
        {/* Sphere B — purple strand */}
        <div
          className="dna-sphere dna-sphere-b"
          style={{ animationDelay: `${delay}s`, left: "50%", marginLeft: -9 }}
        />
      </div>
    );
  });

  const particles = PARTICLES.map((p, i) => (
    <div
      key={`p-${i}`}
      className={`dna-particle ${p.isDrift ? "dna-particle-drift" : "dna-particle-twinkle"}`}
      style={{
        width: p.size,
        height: p.size,
        left: `${p.left}%`,
        top: `${p.top}%`,
        background: p.isBlue
          ? "radial-gradient(circle, rgba(0,180,216,0.9), rgba(0,180,216,0) 70%)"
          : "radial-gradient(circle, rgba(139,92,246,0.9), rgba(139,92,246,0) 70%)",
        boxShadow: p.isBlue
          ? "0 0 6px 2px rgba(0,180,216,0.4)"
          : "0 0 6px 2px rgba(139,92,246,0.4)",
        animationDuration: `${p.duration}s`,
        animationDelay: `${p.delay}s`,
      }}
    />
  ));

  const rays = RAYS.map((r, i) => (
    <div
      key={`r-${i}`}
      className="dna-ray"
      style={{
        width: r.w,
        height: r.h,
        left: r.left,
        top: r.top,
        background: r.color,
        animationDuration: `${r.dur}s`,
        animationDelay: `${r.delay}s`,
      }}
    />
  ));

  return (
    <div className="dna-container">
      {rays}
      <div className="dna-helix">{rungs}</div>
      {particles}
    </div>
  );
}

export default DnaAnimation;
