import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";
import "./EmergencyCases.css";

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

function formatDate(dateStr) {
  if (!dateStr) return "";
  const d = new Date(dateStr);
  return d.toLocaleDateString("en-IN", {
    day: "numeric",
    month: "short",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  });
}

const EMPTY_PATIENT = {
  full_name: "",
  age: "",
  gender: "",
  blood_group: "",
  allergies: "",
  past_surgeries: "",
  diabetes: false,
  hypertension: false,
  heart_disease: false,
  asthma: false,
  chronic_kidney_disease: false,
  previous_stroke: false,
  smoker: false,
  obese: false,
  previous_heart_attack: false,
  previous_hospitalization: false,
};

function Inpatients() {
  const [data, setData] = useState([]);
  const [nurseName, setNurseName] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Patient states
  const [patientType, setPatientType] = useState("existing");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [newPatient, setNewPatient] = useState({ ...EMPTY_PATIENT });
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    fetch("http://localhost:8000/api/user-role/", {
      credentials: "include",
    })
      .then((res) => {
        if (!res.ok) { navigate("/"); return null; }
        return res.json();
      })
      .then((roleData) => {
        if (!roleData) return;
        if (roleData.role !== "nurse") { navigate("/"); return; }
        setNurseName(roleData.name || "");
      });
  }, [navigate]);

  useEffect(() => {
    fetch("http://localhost:8000/api/nurse-dashboard/", {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((result) => {
        if (Array.isArray(result)) {
          const inpatients = result.filter((item) => {
            const risk = (item.predicted_risk || "").toLowerCase();
            return risk === "low" || risk === "moderate" || risk === "medium";
          });
          setData(inpatients);
        }
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleLogout = async () => {
    const csrfToken = getCookie("csrftoken");
    await fetch("http://localhost:8000/api/logout/", {
      method: "POST",
      credentials: "include",
      headers: { "X-CSRFToken": csrfToken },
    });
    navigate("/");
  };

  // ---------- Patient Search ----------
  useEffect(() => {
    if (patientType !== "existing" || searchQuery.trim().length < 2) {
      setSearchResults([]);
      return;
    }

    const timer = setTimeout(() => {
      fetch(
        `http://localhost:8000/api/patient/search/?q=${encodeURIComponent(searchQuery)}`,
        { credentials: "include" },
      )
        .then((res) => res.json())
        .then((d) => {
          if (Array.isArray(d)) setSearchResults(d);
        })
        .catch(() => {});
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery, patientType]);

  const selectPatient = (patient) => {
    setSelectedPatient(patient);
    setSearchQuery("");
    setSearchResults([]);
  };

  // ---------- New Patient ----------
  const handleNewPatientChange = (field, value) => {
    setNewPatient((prev) => ({ ...prev, [field]: value }));
  };

  const handleCreatePatient = async () => {
    if (!newPatient.full_name.trim() || !newPatient.age || !newPatient.gender) {
      alert("Name, age, and gender are required.");
      return;
    }

    setSaving(true);
    try {
      const csrfToken = getCookie("csrftoken");
      const res = await fetch("http://localhost:8000/api/patient/create/", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          "X-CSRFToken": csrfToken,
        },
        body: JSON.stringify(newPatient),
      });

      const result = await res.json();
      if (res.ok) {
        setSelectedPatient({ ...newPatient, id: result.id });
        setNewPatient({ ...EMPTY_PATIENT });
      } else {
        alert(result.error || "Failed to create patient.");
      }
    } catch {
      alert("Error connecting to server.");
    }
    setSaving(false);
  };

  if (loading) return <div className="nurse-loading">Loading...</div>;

  return (
    <div className="nurse-page">
      <header className="nurse-header">
        <div className="nurse-header-left">
          <svg viewBox="0 0 48 48" xmlns="http://www.w3.org/2000/svg">
            <rect x="18" y="4" width="12" height="30" rx="3" fill="white" />
            <rect x="6" y="12" width="36" height="12" rx="3" fill="white" />
            <ellipse cx="36" cy="38" rx="6" ry="3" fill="none" stroke="white" strokeWidth="1.5" />
            <ellipse cx="36" cy="42" rx="6" ry="3" fill="none" stroke="white" strokeWidth="1.5" />
          </svg>
          <span>Apex Health</span>
        </div>
        <div className="nurse-header-right">
          <span className="nurse-name">
            Nurse: <strong>{nurseName}</strong>
          </span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <main className="nurse-main">
        <button className="back-btn" onClick={() => navigate("/nurse")}>
          Back to Dashboard
        </button>

        {/* ===== Patient Selection ===== */}
        <h2 className="section-title">Patient</h2>

        {!selectedPatient ? (
          <>
            <div className="patient-toggle">
              <button
                className={`toggle-btn ${patientType === "existing" ? "active" : ""}`}
                onClick={() => {
                  setPatientType("existing");
                  setNewPatient({ ...EMPTY_PATIENT });
                }}
              >
                Existing Patient
              </button>
              <button
                className={`toggle-btn ${patientType === "new" ? "active" : ""}`}
                onClick={() => {
                  setPatientType("new");
                  setSearchQuery("");
                  setSearchResults([]);
                }}
              >
                New Patient
              </button>
            </div>

            {patientType === "existing" && (
              <div className="patient-search">
                <input
                  type="text"
                  placeholder="Search by patient name..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
                {searchResults.length > 0 && (
                  <div className="search-results">
                    {searchResults.map((p) => (
                      <div
                        key={p.id}
                        className="search-result-item"
                        onClick={() => selectPatient(p)}
                      >
                        <strong>{p.full_name}</strong>
                        <span>
                          {p.age} yrs · {p.gender}
                        </span>
                        {p.blood_group && <span> · {p.blood_group}</span>}
                      </div>
                    ))}
                  </div>
                )}
                {searchQuery.trim().length >= 2 && searchResults.length === 0 && (
                  <p className="no-results">
                    No patients found. Try a different name or add a new patient.
                  </p>
                )}
              </div>
            )}

            {patientType === "new" && (
              <div className="new-patient-form">
                <div className="form-row">
                  <div className="form-field">
                    <label>Full Name *</label>
                    <input
                      type="text"
                      value={newPatient.full_name}
                      onChange={(e) => handleNewPatientChange("full_name", e.target.value)}
                    />
                  </div>
                  <div className="form-field">
                    <label>Age *</label>
                    <input
                      type="number"
                      value={newPatient.age}
                      onChange={(e) => handleNewPatientChange("age", e.target.value)}
                    />
                  </div>
                  <div className="form-field">
                    <label>Gender *</label>
                    <select
                      value={newPatient.gender}
                      onChange={(e) => handleNewPatientChange("gender", e.target.value)}
                    >
                      <option value="">Select</option>
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Other">Other</option>
                    </select>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-field">
                    <label>Blood Group</label>
                    <select
                      value={newPatient.blood_group}
                      onChange={(e) => handleNewPatientChange("blood_group", e.target.value)}
                    >
                      <option value="">Select</option>
                      <option value="A+">A+</option>
                      <option value="A-">A-</option>
                      <option value="B+">B+</option>
                      <option value="B-">B-</option>
                      <option value="O+">O+</option>
                      <option value="O-">O-</option>
                      <option value="AB+">AB+</option>
                      <option value="AB-">AB-</option>
                    </select>
                  </div>
                  <div className="form-field">
                    <label>Allergies</label>
                    <input
                      type="text"
                      value={newPatient.allergies}
                      onChange={(e) => handleNewPatientChange("allergies", e.target.value)}
                      placeholder="e.g. Penicillin, Peanuts"
                    />
                  </div>
                  <div className="form-field">
                    <label>Past Surgeries</label>
                    <input
                      type="text"
                      value={newPatient.past_surgeries}
                      onChange={(e) => handleNewPatientChange("past_surgeries", e.target.value)}
                      placeholder="e.g. Appendectomy"
                    />
                  </div>
                </div>

                <div className="medical-history">
                  <label className="history-heading">Medical History</label>
                  <div className="checkbox-grid">
                    {[
                      ["diabetes", "Diabetes"],
                      ["hypertension", "Hypertension"],
                      ["heart_disease", "Heart Disease"],
                      ["asthma", "Asthma"],
                      ["chronic_kidney_disease", "Chronic Kidney Disease"],
                      ["previous_stroke", "Previous Stroke"],
                      ["smoker", "Smoker"],
                      ["obese", "Obese"],
                      ["previous_heart_attack", "Previous Heart Attack"],
                      ["previous_hospitalization", "Previous Hospitalization"],
                    ].map(([field, label]) => (
                      <label key={field} className="checkbox-label">
                        <input
                          type="checkbox"
                          checked={newPatient[field]}
                          onChange={(e) => handleNewPatientChange(field, e.target.checked)}
                        />
                        {label}
                      </label>
                    ))}
                  </div>
                </div>

                <button
                  className="voice-btn voice-btn-submit"
                  onClick={handleCreatePatient}
                  disabled={saving}
                >
                  {saving ? "Saving..." : "Register Patient"}
                </button>
              </div>
            )}
          </>
        ) : (
          <div className="selected-patient">
            <div className="selected-patient-info">
              <strong>{selectedPatient.full_name}</strong>
              <span>
                {selectedPatient.age} yrs · {selectedPatient.gender}
              </span>
              {selectedPatient.blood_group && (
                <span> · {selectedPatient.blood_group}</span>
              )}
            </div>
            <button
              className="voice-btn voice-btn-clear"
              onClick={() => setSelectedPatient(null)}
            >
              Change Patient
            </button>
          </div>
        )}

        {/* ===== Inpatient Cases (only after patient selected) ===== */}
        {selectedPatient && (
          <>
            <h2 className="section-title" style={{ color: "#818cf8", marginTop: 32 }}>
              Inpatient Records
            </h2>

            {data.length === 0 ? (
              <p className="no-data">No inpatient cases found.</p>
            ) : (
              <div className="triage-grid">
                {data.map((item) => (
                  <div
                    key={item.id}
                    className="triage-card"
                    style={{ borderColor: "rgba(67, 97, 238, 0.2)" }}
                  >
                    <div className="triage-card-header">
                      <div>
                        <div className="patient-name">
                          {item.patient_name || `Patient #${item.id}`}
                        </div>
                        <div className="patient-meta">
                          {item.patient_age && `${item.patient_age} yrs`}
                          {item.patient_gender && ` · ${item.patient_gender}`}
                        </div>
                      </div>
                      <span
                        className={`risk-badge ${
                          (item.predicted_risk || "").toLowerCase() === "low"
                            ? "risk-low"
                            : "risk-moderate"
                        }`}
                      >
                        {item.predicted_risk || "Pending"}
                      </span>
                    </div>

                    <div className="vitals-grid">
                      <div className="vital-item">
                        <div className="vital-label">BP</div>
                        <div className="vital-value">{item.systolic_bp || "—"}</div>
                      </div>
                      <div className="vital-item">
                        <div className="vital-label">HR</div>
                        <div className="vital-value">{item.heart_rate || "—"}</div>
                      </div>
                      <div className="vital-item">
                        <div className="vital-label">Temp</div>
                        <div className="vital-value">
                          {item.temperature ? `${item.temperature}°` : "—"}
                        </div>
                      </div>
                      <div className="vital-item">
                        <div className="vital-label">O2</div>
                        <div className="vital-value">
                          {item.oxygen ? `${item.oxygen}%` : "—"}
                        </div>
                      </div>
                    </div>

                    <div className="triage-card-footer">
                      <span className="department">
                        {item.recommended_department || "Unassigned"}
                      </span>
                      <span className="timestamp">{formatDate(item.created_at)}</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </>
        )}
      </main>
    </div>
  );
}

export default Inpatients;
