import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";
import "./EmergencyCases.css";

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

function formatDate(dateStr) {
  if (!dateStr) return "";
  const d = new Date(dateStr);
  return d.toLocaleDateString("en-IN", {
    day: "numeric", month: "short", year: "numeric", hour: "2-digit", minute: "2-digit",
  });
}

const EMPTY_PATIENT = {
  full_name: "", age: "", gender: "", blood_group: "",
  allergies: "", past_surgeries: "",
  diabetes: false, hypertension: false, heart_disease: false, asthma: false,
  chronic_kidney_disease: false, previous_stroke: false, smoker: false, obese: false,
  previous_heart_attack: false, previous_hospitalization: false,
};

function Inpatients() {
  const [data, setData] = useState([]);
  const [nurseName, setNurseName] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Patient states
  const [patientType, setPatientType] = useState("existing");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [newPatient, setNewPatient] = useState({ ...EMPTY_PATIENT });
  const [saving, setSaving] = useState(false);

  // Input mode: "manual" | "pdf" | "voice"
  const [inputMode, setInputMode] = useState(null);

  // Symptoms text (shared for voice/pdf)
  const [transcript, setTranscript] = useState("");

  // Manual vitals + symptoms form (TriageRequest model)
  const [vitals, setVitals] = useState({
    systolic_bp: "", heart_rate: "", temperature: "", oxygen: "",
  });
  const [symptoms, setSymptoms] = useState({
    chest_pain: false, severe_breathlessness: false, sudden_confusion: false,
    stroke_symptoms: false, seizure: false, severe_trauma: false,
    uncontrolled_bleeding: false, loss_of_consciousness: false,
    severe_allergic_reaction: false,
    persistent_fever: false, vomiting: false, moderate_abdominal_pain: false,
    persistent_cough: false, moderate_breathlessness: false,
    severe_headache: false, dizziness: false, dehydration: false,
    palpitations: false, migraine: false,
    mild_headache: false, sore_throat: false, runny_nose: false,
    mild_cough: false, fatigue: false, body_ache: false,
    mild_abdominal_pain: false, skin_rash: false,
    mild_back_pain: false, mild_joint_pain: false,
  });
  const [manualSubmitting, setManualSubmitting] = useState(false);
  const [manualResult, setManualResult] = useState(null);

  // Voice states
  const [isListening, setIsListening] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [translating, setTranslating] = useState(false);

  // PDF states
  const [pdfFile, setPdfFile] = useState(null);
  const [pdfExtracting, setPdfExtracting] = useState(false);

  // Triage states
  const [triageResult, setTriageResult] = useState(null);
  const [triageLoading, setTriageLoading] = useState(false);

  useEffect(() => {
    fetch("http://localhost:8000/api/user-role/", { credentials: "include" })
      .then((res) => {
        if (!res.ok) { navigate("/"); return null; }
        return res.json();
      })
      .then((roleData) => {
        if (!roleData) return;
        if (roleData.role !== "nurse") { navigate("/"); return; }
        setNurseName(roleData.name || "");
      });
  }, [navigate]);

  useEffect(() => {
    fetch("http://localhost:8000/api/nurse-dashboard/", { credentials: "include" })
      .then((res) => res.json())
      .then((result) => {
        if (Array.isArray(result)) {
          const inpatients = result.filter((item) => {
            const risk = (item.predicted_risk || "").toLowerCase();
            return risk === "low" || risk === "moderate" || risk === "medium";
          });
          setData(inpatients);
        }
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleLogout = async () => {
    const csrfToken = getCookie("csrftoken");
    await fetch("http://localhost:8000/api/logout/", {
      method: "POST", credentials: "include",
      headers: { "X-CSRFToken": csrfToken },
    });
    navigate("/");
  };

  // ---------- Patient Search ----------
  useEffect(() => {
    if (patientType !== "existing" || searchQuery.trim().length < 2) {
      setSearchResults([]); return;
    }
    const timer = setTimeout(() => {
      fetch(`http://localhost:8000/api/patient/search/?q=${encodeURIComponent(searchQuery)}`,
        { credentials: "include" })
        .then((res) => res.json())
        .then((d) => { if (Array.isArray(d)) setSearchResults(d); })
        .catch(() => {});
    }, 300);
    return () => clearTimeout(timer);
  }, [searchQuery, patientType]);

  const selectPatient = (patient) => {
    setSelectedPatient(patient); setSearchQuery(""); setSearchResults([]);
  };

  // ---------- New Patient ----------
  const handleNewPatientChange = (field, value) => {
    setNewPatient((prev) => ({ ...prev, [field]: value }));
  };

  const handleCreatePatient = async () => {
    if (!newPatient.full_name.trim() || !newPatient.age || !newPatient.gender) {
      alert("Name, age, and gender are required."); return;
    }
    setSaving(true);
    try {
      const csrfToken = getCookie("csrftoken");
      const res = await fetch("http://localhost:8000/api/patient/create/", {
        method: "POST", credentials: "include",
        headers: { "Content-Type": "application/json", "X-CSRFToken": csrfToken },
        body: JSON.stringify(newPatient),
      });
      const result = await res.json();
      if (res.ok) {
        setSelectedPatient({ ...newPatient, id: result.id });
        setNewPatient({ ...EMPTY_PATIENT });
      } else { alert(result.error || "Failed to create patient."); }
    } catch { alert("Error connecting to server."); }
    setSaving(false);
  };

  // ---------- Voice ----------
  const startListening = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      setMediaRecorder(recorder);
      const chunks = [];
      recorder.ondataavailable = (e) => { if (e.data.size > 0) chunks.push(e.data); };
      recorder.onstop = async () => {
        const audioBlob = new Blob(chunks, { type: "audio/webm" });
        const formData = new FormData();
        formData.append("audio", audioBlob, "recording.webm");
        setTranslating(true);
        try {
          const response = await fetch("http://localhost:8000/api/whisper/", {
            method: "POST", credentials: "include", body: formData,
          });
          const d = await response.json();
          if (d.translated_text) setTranscript(d.translated_text);
          else alert(d.error || "Transcription failed.");
        } catch { alert("Error sending audio to server."); }
        setTranslating(false);
      };
      recorder.start();
      setIsListening(true);
    } catch { alert("Microphone access denied."); }
  };

  const stopListening = () => {
    if (mediaRecorder) { mediaRecorder.stop(); setIsListening(false); }
  };

  // ---------- PDF Upload ----------
  const handlePdfUpload = async () => {
    if (!pdfFile) return;
    setPdfExtracting(true);
    const formData = new FormData();
    formData.append("file", pdfFile);
    if (selectedPatient) formData.append("patient_id", selectedPatient.id);

    try {
      const res = await fetch("http://localhost:8000/api/inpatient-analyze/", {
        method: "POST", credentials: "include", body: formData,
      });
      const result = await res.json();
      if (result.received_text) {
        setTranscript(result.received_text);
      } else if (result.summary) {
        setTranscript(result.summary);
      } else {
        alert("Could not extract text from PDF.");
      }
    } catch { alert("Error uploading PDF."); }
    setPdfExtracting(false);
  };

  // ---------- Triage ----------
  const handleTriageSubmit = async () => {
    if (!transcript.trim()) return;
    setTriageLoading(true);
    setTriageResult(null);
    try {
      const csrfToken = getCookie("csrftoken");
      const res = await fetch("http://localhost:8000/api/triage/", {
        method: "POST", credentials: "include",
        headers: { "Content-Type": "application/json", "X-CSRFToken": csrfToken },
        body: JSON.stringify({ symptoms: transcript }),
      });
      const result = await res.json();
      setTriageResult(result);
    } catch { setTriageResult({ error: "Failed to reach server." }); }
    setTriageLoading(false);
  };

  // ---------- Manual Triage Request Submit ----------
  const handleManualSubmit = async () => {
    if (!selectedPatient) return;
    if (!vitals.systolic_bp || !vitals.heart_rate || !vitals.temperature || !vitals.oxygen) {
      alert("All vitals (BP, Heart Rate, Temperature, O2) are required.");
      return;
    }
    setManualSubmitting(true);
    setManualResult(null);
    try {
      const csrfToken = getCookie("csrftoken");
      const res = await fetch("http://localhost:8000/api/triage-request/create/", {
        method: "POST", credentials: "include",
        headers: { "Content-Type": "application/json", "X-CSRFToken": csrfToken },
        body: JSON.stringify({
          patient_id: selectedPatient.id,
          ...vitals,
          ...symptoms,
        }),
      });
      const result = await res.json();
      if (res.ok) {
        setManualResult(result);
      } else {
        alert(result.error || "Failed to create triage request.");
      }
    } catch { alert("Error connecting to server."); }
    setManualSubmitting(false);
  };

  const clearAll = () => {
    setTranscript(""); setTriageResult(null); setPdfFile(null);
    setManualResult(null);
    setVitals({ systolic_bp: "", heart_rate: "", temperature: "", oxygen: "" });
    setSymptoms(Object.fromEntries(Object.keys(symptoms).map(k => [k, false])));
  };

  if (loading) return <div className="nurse-loading">Loading...</div>;

  // ---------- Render helpers ----------
  const renderNewPatientForm = () => (
    <div className="new-patient-form">
      <div className="form-row">
        <div className="form-field"><label>Full Name *</label>
          <input type="text" value={newPatient.full_name} onChange={(e) => handleNewPatientChange("full_name", e.target.value)} /></div>
        <div className="form-field"><label>Age *</label>
          <input type="number" value={newPatient.age} onChange={(e) => handleNewPatientChange("age", e.target.value)} /></div>
        <div className="form-field"><label>Gender *</label>
          <select value={newPatient.gender} onChange={(e) => handleNewPatientChange("gender", e.target.value)}>
            <option value="">Select</option><option value="Male">Male</option>
            <option value="Female">Female</option><option value="Other">Other</option>
          </select></div>
      </div>
      <div className="form-row">
        <div className="form-field"><label>Blood Group</label>
          <select value={newPatient.blood_group} onChange={(e) => handleNewPatientChange("blood_group", e.target.value)}>
            <option value="">Select</option>
            {["A+","A-","B+","B-","O+","O-","AB+","AB-"].map(bg => <option key={bg} value={bg}>{bg}</option>)}
          </select></div>
        <div className="form-field"><label>Allergies</label>
          <input type="text" value={newPatient.allergies} onChange={(e) => handleNewPatientChange("allergies", e.target.value)} placeholder="e.g. Penicillin" /></div>
        <div className="form-field"><label>Past Surgeries</label>
          <input type="text" value={newPatient.past_surgeries} onChange={(e) => handleNewPatientChange("past_surgeries", e.target.value)} placeholder="e.g. Appendectomy" /></div>
      </div>
      <div className="medical-history">
        <label className="history-heading">Medical History</label>
        <div className="checkbox-grid">
          {[["diabetes","Diabetes"],["hypertension","Hypertension"],["heart_disease","Heart Disease"],
            ["asthma","Asthma"],["chronic_kidney_disease","Chronic Kidney Disease"],["previous_stroke","Previous Stroke"],
            ["smoker","Smoker"],["obese","Obese"],["previous_heart_attack","Previous Heart Attack"],
            ["previous_hospitalization","Previous Hospitalization"]].map(([field, label]) => (
            <label key={field} className="checkbox-label">
              <input type="checkbox" checked={newPatient[field]} onChange={(e) => handleNewPatientChange(field, e.target.checked)} />
              {label}
            </label>
          ))}
        </div>
      </div>
      <button className="voice-btn voice-btn-submit" onClick={handleCreatePatient} disabled={saving}>
        {saving ? "Saving..." : "Register Patient"}
      </button>
    </div>
  );

  return (
    <div className="nurse-page">
      <header className="nurse-header">
        <div className="nurse-header-left">
          <svg viewBox="0 0 48 48" xmlns="http://www.w3.org/2000/svg">
            <rect x="18" y="4" width="12" height="30" rx="3" fill="white" />
            <rect x="6" y="12" width="36" height="12" rx="3" fill="white" />
            <ellipse cx="36" cy="38" rx="6" ry="3" fill="none" stroke="white" strokeWidth="1.5" />
            <ellipse cx="36" cy="42" rx="6" ry="3" fill="none" stroke="white" strokeWidth="1.5" />
          </svg>
          <span>Apex Health</span>
        </div>
        <div className="nurse-header-right">
          <span className="nurse-name">Nurse: <strong>{nurseName}</strong></span>
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </div>
      </header>

      <main className="nurse-main">
        <button className="back-btn" onClick={() => navigate("/nurse")}>Back to Dashboard</button>

        {/* ===== Patient Selection ===== */}
        <h2 className="section-title">Patient</h2>

        {!selectedPatient ? (
          <>
            <div className="patient-toggle">
              <button className={`toggle-btn ${patientType === "existing" ? "active" : ""}`}
                onClick={() => { setPatientType("existing"); setNewPatient({ ...EMPTY_PATIENT }); }}>
                Existing Patient
              </button>
              <button className={`toggle-btn ${patientType === "new" ? "active" : ""}`}
                onClick={() => { setPatientType("new"); setSearchQuery(""); setSearchResults([]); }}>
                New Patient
              </button>
            </div>

            {patientType === "existing" && (
              <div className="patient-search">
                <input type="text" placeholder="Search by patient name..." value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)} className="search-input" />
                {searchResults.length > 0 && (
                  <div className="search-results">
                    {searchResults.map((p) => (
                      <div key={p.id} className="search-result-item" onClick={() => selectPatient(p)}>
                        <strong>{p.full_name}</strong>
                        <span>{p.age} yrs · {p.gender}</span>
                        {p.blood_group && <span> · {p.blood_group}</span>}
                      </div>
                    ))}
                  </div>
                )}
                {searchQuery.trim().length >= 2 && searchResults.length === 0 && (
                  <p className="no-results">No patients found.</p>
                )}
              </div>
            )}

            {patientType === "new" && renderNewPatientForm()}
          </>
        ) : (
          <div className="selected-patient">
            <div className="selected-patient-info">
              <strong>{selectedPatient.full_name}</strong>
              <span>{selectedPatient.age} yrs · {selectedPatient.gender}</span>
              {selectedPatient.blood_group && <span> · {selectedPatient.blood_group}</span>}
            </div>
            <button className="voice-btn voice-btn-clear"
              onClick={() => { setSelectedPatient(null); setInputMode(null); clearAll(); }}>
              Change Patient
            </button>
          </div>
        )}

        {/* ===== Input Mode Selection ===== */}
        {selectedPatient && (
          <>
            <div style={{ marginTop: 24 }}>
              <h2 className="section-title" style={{ color: "#818cf8" }}>Symptom Input</h2>
              <div className="patient-toggle">
                <button className={`toggle-btn ${inputMode === "manual" ? "active" : ""}`}
                  onClick={() => { setInputMode("manual"); clearAll(); }}>
                  Manual Entry
                </button>
                <button className={`toggle-btn ${inputMode === "pdf" ? "active" : ""}`}
                  onClick={() => { setInputMode("pdf"); clearAll(); }}>
                  PDF Upload
                </button>
                <button className={`toggle-btn ${inputMode === "voice" ? "active" : ""}`}
                  onClick={() => { setInputMode("voice"); clearAll(); }}>
                  Voice Input
                </button>
              </div>
            </div>

            {/* Manual */}
            {inputMode === "manual" && (
              <div className="voice-transcript" style={{ marginTop: 16 }}>
                <label>Describe Symptoms:</label>
                <textarea value={transcript} onChange={(e) => setTranscript(e.target.value)} rows={4}
                  placeholder="Type the patient's symptoms here..." />
                <button className="voice-btn voice-btn-submit" onClick={handleTriageSubmit}
                  disabled={triageLoading || !transcript.trim()}>
                  {triageLoading ? "Analyzing..." : "Analyze Symptoms"}
                </button>
              </div>
            )}

            {/* PDF */}
            {inputMode === "pdf" && (
              <div className="pdf-upload" style={{ marginTop: 16 }}>
                <label className="override-label">Upload Medical Report (PDF):</label>
                <div style={{ display: "flex", gap: 12, alignItems: "center", flexWrap: "wrap" }}>
                  <input type="file" accept=".pdf" className="search-input"
                    style={{ maxWidth: 300 }}
                    onChange={(e) => setPdfFile(e.target.files[0] || null)} />
                  <button className="voice-btn voice-btn-submit" style={{ marginTop: 0 }}
                    onClick={handlePdfUpload} disabled={!pdfFile || pdfExtracting}>
                    {pdfExtracting ? "Extracting..." : "Extract & Analyze"}
                  </button>
                </div>
                {transcript && (
                  <div className="voice-transcript" style={{ marginTop: 16 }}>
                    <label>Extracted Text:</label>
                    <textarea value={transcript} onChange={(e) => setTranscript(e.target.value)} rows={4} />
                    <button className="voice-btn voice-btn-submit" onClick={handleTriageSubmit}
                      disabled={triageLoading || !transcript.trim()}>
                      {triageLoading ? "Analyzing..." : "Analyze Symptoms"}
                    </button>
                  </div>
                )}
              </div>
            )}

            {/* Voice */}
            {inputMode === "voice" && (
              <>
                <div className="voice-controls" style={{ marginTop: 16 }}>
                  {isListening ? (
                    <button className="voice-btn voice-btn-stop" onClick={stopListening}>Stop Recording</button>
                  ) : (
                    <button className="voice-btn voice-btn-start" onClick={startListening}>Start Recording</button>
                  )}
                  {transcript && (
                    <button className="voice-btn voice-btn-clear" onClick={clearAll}>Clear</button>
                  )}
                </div>
                {isListening && <div className="voice-indicator">Listening...</div>}
                {translating && (
                  <div className="voice-translating">
                    <div className="translating-spinner"></div><span>Translating audio...</span>
                  </div>
                )}
                {transcript && (
                  <div className="voice-transcript">
                    <label>Translated Text:</label>
                    <textarea value={transcript} onChange={(e) => setTranscript(e.target.value)} rows={3} />
                    <button className="voice-btn voice-btn-submit" onClick={handleTriageSubmit}
                      disabled={triageLoading}>
                      {triageLoading ? "Analyzing..." : "Analyze Symptoms"}
                    </button>
                  </div>
                )}
              </>
            )}

            {/* Triage Result */}
            {triageResult && triageResult.error && (
              <p className="voice-error">{triageResult.error}</p>
            )}
            {triageResult && !triageResult.error && (
              <div className="triage-result">
                <div><strong>Department:</strong> {triageResult.assigned_department.replace("_", " ")}</div>
                <div><strong>Confidence:</strong> {Math.round(triageResult.confidence * 100)}%</div>
              </div>
            )}

            {/* Existing inpatient records */}
            <h2 className="section-title" style={{ color: "#818cf8", marginTop: 32 }}>
              Inpatient Records
            </h2>
            {data.length === 0 ? (
              <p className="no-data">No inpatient cases found.</p>
            ) : (
              <div className="triage-grid">
                {data.map((item) => (
                  <div key={item.id} className="triage-card" style={{ borderColor: "rgba(67, 97, 238, 0.2)" }}>
                    <div className="triage-card-header">
                      <div>
                        <div className="patient-name">{item.patient_name || `Patient #${item.id}`}</div>
                        <div className="patient-meta">
                          {item.patient_age && `${item.patient_age} yrs`}
                          {item.patient_gender && ` · ${item.patient_gender}`}
                        </div>
                      </div>
                      <span className={`risk-badge ${(item.predicted_risk || "").toLowerCase() === "low" ? "risk-low" : "risk-moderate"}`}>
                        {item.predicted_risk || "Pending"}
                      </span>
                    </div>
                    <div className="vitals-grid">
                      <div className="vital-item"><div className="vital-label">BP</div><div className="vital-value">{item.systolic_bp || "—"}</div></div>
                      <div className="vital-item"><div className="vital-label">HR</div><div className="vital-value">{item.heart_rate || "—"}</div></div>
                      <div className="vital-item"><div className="vital-label">Temp</div><div className="vital-value">{item.temperature ? `${item.temperature}°` : "—"}</div></div>
                      <div className="vital-item"><div className="vital-label">O2</div><div className="vital-value">{item.oxygen ? `${item.oxygen}%` : "—"}</div></div>
                    </div>
                    <div className="triage-card-footer">
                      <span className="department">{item.recommended_department || "Unassigned"}</span>
                      <span className="timestamp">{formatDate(item.created_at)}</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </>
        )}
      </main>
    </div>
  );
}

export default Inpatients;
