import { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";
import "./EmergencyCases.css";

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

function formatDate(dateStr) {
  if (!dateStr) return "";
  const d = new Date(dateStr);
  return d.toLocaleDateString("en-IN", {
    day: "numeric",
    month: "short",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  });
}

function EmergencyCases() {
  const [data, setData] = useState([]);
  const [nurseName, setNurseName] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Voice state
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [triageResult, setTriageResult] = useState(null);
  const [triageLoading, setTriageLoading] = useState(false);
  const recognitionRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:8000/api/user-role/", {
      credentials: "include",
    })
      .then((res) => {
        if (!res.ok) {
          navigate("/");
          return null;
        }
        return res.json();
      })
      .then((roleData) => {
        if (!roleData) return;
        if (roleData.role !== "nurse") {
          navigate("/");
          return;
        }
        setNurseName(roleData.name || "");
      });
  }, [navigate]);

  useEffect(() => {
    fetch("http://localhost:8000/api/nurse-dashboard/", {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((result) => {
        if (Array.isArray(result)) {
          const emergency = result.filter((item) => {
            const risk = (item.predicted_risk || "").toLowerCase();
            return risk === "high" || risk === "critical";
          });
          setData(emergency);
        }
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleLogout = async () => {
    const csrfToken = getCookie("csrftoken");
    await fetch("http://localhost:8000/api/logout/", {
      method: "POST",
      credentials: "include",
      headers: { "X-CSRFToken": csrfToken },
    });
    navigate("/");
  };

  // Voice recognition
  const startListening = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert("Speech recognition is not supported in this browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-IN"; // change if needed
    recognition.continuous = false;
    recognition.interimResults = false;

    recognition.onresult = (event) => {
      const speechToText = event.results[0][0].transcript;
      setTranscript((prev) => prev + " " + speechToText);
    };

    recognition.onerror = (event) => {
      console.error("Speech error:", event.error);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognitionRef.current = recognition;
    recognition.start();
    setIsListening(true);
  };
  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
    setIsListening(false);
  };

  const handleTriageSubmit = async () => {
    if (!transcript.trim()) return;

    setTriageLoading(true);
    setTriageResult(null);

    try {
      const csrfToken = getCookie("csrftoken");
      const res = await fetch("http://localhost:8000/api/triage/", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          "X-CSRFToken": csrfToken,
        },
        body: JSON.stringify({ symptoms: transcript }),
      });
      const result = await res.json();
      setTriageResult(result);
    } catch {
      setTriageResult({ error: "Failed to reach server." });
    }
    setTriageLoading(false);
  };

  const clearVoice = () => {
    window.location.reload();
  };

  if (loading) return <div className="nurse-loading">Loading...</div>;

  return (
    <div className="nurse-page">
      <header className="nurse-header">
        <div className="nurse-header-left">
          <svg viewBox="0 0 48 48" xmlns="http://www.w3.org/2000/svg">
            <rect x="18" y="4" width="12" height="30" rx="3" fill="white" />
            <rect x="6" y="12" width="36" height="12" rx="3" fill="white" />
            <ellipse
              cx="36"
              cy="38"
              rx="6"
              ry="3"
              fill="none"
              stroke="white"
              strokeWidth="1.5"
            />
            <ellipse
              cx="36"
              cy="42"
              rx="6"
              ry="3"
              fill="none"
              stroke="white"
              strokeWidth="1.5"
            />
          </svg>
          <span>Apex Health</span>
        </div>
        <div className="nurse-header-right">
          <span className="nurse-name">
            Nurse: <strong>{nurseName}</strong>
          </span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <main className="nurse-main">
        <button className="back-btn" onClick={() => navigate("/nurse")}>
          Back to Dashboard
        </button>

        <h2 className="section-title" style={{ color: "#f87171" }}>
          Emergency Cases
        </h2>

        {/* Voice Triage Section */}
        <div className="voice-section">
          <h3 className="voice-title">Voice Triage</h3>
          <p className="voice-desc">
            Describe the patient's symptoms using your voice. The AI will
            recommend a department.
          </p>

          <div className="voice-controls">
            {isListening ? (
              <button
                className="voice-btn voice-btn-stop"
                onClick={stopListening}
              >
                <svg
                  viewBox="0 0 24 24"
                  fill="currentColor"
                  width="20"
                  height="20"
                >
                  <rect x="6" y="6" width="12" height="12" rx="2" />
                </svg>
                Stop Recording
              </button>
            ) : (
              <button
                className="voice-btn voice-btn-start"
                onClick={startListening}
              >
                <svg
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <path d="M12 1a3 3 0 0 0-3 3v8a3 3 0 0 0 6 0V4a3 3 0 0 0-3-3z" />
                  <path d="M19 10v2a7 7 0 0 1-14 0v-2" />
                  <line x1="12" y1="19" x2="12" y2="23" />
                  <line x1="8" y1="23" x2="16" y2="23" />
                </svg>
                Start Recording
              </button>
            )}

            {transcript && (
              <button
                className="voice-btn voice-btn-clear"
                onClick={clearVoice}
              >
                Clear
              </button>
            )}
          </div>

          {isListening && (
            <div className="voice-indicator">
              <span className="pulse-dot" />
              Listening...
            </div>
          )}

          {transcript && (
            <div className="voice-transcript">
              <label>Transcript:</label>
              <textarea
                value={transcript}
                onChange={(e) => setTranscript(e.target.value)}
                rows={3}
              />
              <button
                className="voice-btn voice-btn-submit"
                onClick={handleTriageSubmit}
                disabled={triageLoading}
              >
                {triageLoading ? "Analyzing..." : "Analyze Symptoms"}
              </button>
            </div>
          )}

          {triageResult && !triageResult.error && (
            <div className="triage-result">
              <div className="triage-result-dept">
                <span className="triage-result-label">
                  Recommended Department
                </span>
                <span className="triage-result-value">
                  {triageResult.assigned_department}
                </span>
              </div>
              <div className="triage-result-conf">
                <span className="triage-result-label">Confidence</span>
                <span className="triage-result-value">
                  {Math.round(triageResult.confidence * 100)}%
                </span>
              </div>
            </div>
          )}

          {triageResult && triageResult.error && (
            <p className="voice-error">{triageResult.error}</p>
          )}
        </div>

        {/* Emergency Cases List */}
        <h3
          className="section-title"
          style={{ color: "#f87171", marginTop: 32 }}
        >
          Active Emergency Cases
        </h3>

        {data.length === 0 ? (
          <p className="no-data">No emergency cases found.</p>
        ) : (
          <div className="triage-grid">
            {data.map((item) => (
              <div
                key={item.id}
                className="triage-card"
                style={{ borderColor: "rgba(239, 68, 68, 0.2)" }}
              >
                <div className="triage-card-header">
                  <div>
                    <div className="patient-name">
                      {item.patient_name || `Patient #${item.id}`}
                    </div>
                    <div className="patient-meta">
                      {item.patient_age && `${item.patient_age} yrs`}
                      {item.patient_gender && ` · ${item.patient_gender}`}
                    </div>
                  </div>
                  <span className="risk-badge risk-high">
                    {item.predicted_risk || "Critical"}
                  </span>
                </div>

                <div className="vitals-grid">
                  <div className="vital-item">
                    <div className="vital-label">BP</div>
                    <div className="vital-value">{item.systolic_bp || "—"}</div>
                  </div>
                  <div className="vital-item">
                    <div className="vital-label">HR</div>
                    <div className="vital-value">{item.heart_rate || "—"}</div>
                  </div>
                  <div className="vital-item">
                    <div className="vital-label">Temp</div>
                    <div className="vital-value">
                      {item.temperature ? `${item.temperature}°` : "—"}
                    </div>
                  </div>
                  <div className="vital-item">
                    <div className="vital-label">O2</div>
                    <div className="vital-value">
                      {item.oxygen ? `${item.oxygen}%` : "—"}
                    </div>
                  </div>
                </div>

                <div className="triage-card-footer">
                  <span className="department">
                    {item.recommended_department || "Unassigned"}
                  </span>
                  <span className="timestamp">
                    {formatDate(item.created_at)}
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

export default EmergencyCases;
