import { useRef, useMemo } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { EffectComposer, Bloom } from "@react-three/postprocessing";
import * as THREE from "three";

/* ============================================================
   Helper: generate points along a helix
   ============================================================ */
function helixPoints(radius, height, turns, segments, phase = 0) {
  const pts = [];
  for (let i = 0; i <= segments; i++) {
    const t = i / segments;
    const angle = turns * Math.PI * 2 * t + phase;
    pts.push(
      new THREE.Vector3(
        Math.cos(angle) * radius,
        height * t - height / 2,
        Math.sin(angle) * radius
      )
    );
  }
  return pts;
}

/* ============================================================
   The 3‑D DNA double helix mesh group
   ============================================================ */
function DnaHelix() {
  const groupRef = useRef();

  // Slow continuous rotation
  useFrame((_, delta) => {
    if (groupRef.current) {
      groupRef.current.rotation.y += delta * 0.25;
    }
  });

  const { strand1Geo, strand2Geo, rungs } = useMemo(() => {
    const R = 1.2;       // helix radius
    const H = 12;        // total height
    const TURNS = 3.5;   // number of full twists
    const SEG = 300;     // smoothness

    // Two strands offset by π
    const pts1 = helixPoints(R, H, TURNS, SEG, 0);
    const pts2 = helixPoints(R, H, TURNS, SEG, Math.PI);

    const curve1 = new THREE.CatmullRomCurve3(pts1);
    const curve2 = new THREE.CatmullRomCurve3(pts2);

    const s1 = new THREE.TubeGeometry(curve1, SEG, 0.12, 12, false);
    const s2 = new THREE.TubeGeometry(curve2, SEG, 0.12, 12, false);

    // Base‑pair rungs connecting the two strands
    const rungData = [];
    const RUNG_COUNT = 60;
    for (let i = 0; i < RUNG_COUNT; i++) {
      const t = i / RUNG_COUNT;
      const p1 = curve1.getPoint(t);
      const p2 = curve2.getPoint(t);
      rungData.push({ p1, p2 });
    }

    return { strand1Geo: s1, strand2Geo: s2, rungs: rungData };
  }, []);

  // Shared materials
  const cyanMat = useMemo(
    () =>
      new THREE.MeshStandardMaterial({
        color: new THREE.Color(0.05, 0.4, 0.7),
        emissive: new THREE.Color(0.0, 0.5, 0.9),
        emissiveIntensity: 1.8,
        roughness: 0.3,
        metalness: 0.6,
        transparent: true,
        opacity: 0.92,
      }),
    []
  );

  const purpleMat = useMemo(
    () =>
      new THREE.MeshStandardMaterial({
        color: new THREE.Color(0.3, 0.1, 0.55),
        emissive: new THREE.Color(0.45, 0.15, 0.85),
        emissiveIntensity: 1.6,
        roughness: 0.3,
        metalness: 0.6,
        transparent: true,
        opacity: 0.92,
      }),
    []
  );

  const rungMat = useMemo(
    () =>
      new THREE.MeshStandardMaterial({
        color: new THREE.Color(0.15, 0.25, 0.5),
        emissive: new THREE.Color(0.1, 0.3, 0.65),
        emissiveIntensity: 1.0,
        roughness: 0.5,
        metalness: 0.4,
        transparent: true,
        opacity: 0.6,
      }),
    []
  );

  const sphereMat1 = useMemo(
    () =>
      new THREE.MeshStandardMaterial({
        color: new THREE.Color(0.1, 0.6, 0.85),
        emissive: new THREE.Color(0.0, 0.6, 1.0),
        emissiveIntensity: 2.2,
        roughness: 0.2,
        metalness: 0.7,
      }),
    []
  );

  const sphereMat2 = useMemo(
    () =>
      new THREE.MeshStandardMaterial({
        color: new THREE.Color(0.4, 0.15, 0.7),
        emissive: new THREE.Color(0.5, 0.2, 1.0),
        emissiveIntensity: 2.0,
        roughness: 0.2,
        metalness: 0.7,
      }),
    []
  );

  return (
    <group ref={groupRef} rotation={[0, 0, 0.55]}>
      {/* Strand 1 — cyan tube */}
      <mesh geometry={strand1Geo} material={cyanMat} />

      {/* Strand 2 — purple tube */}
      <mesh geometry={strand2Geo} material={purpleMat} />

      {/* Base‑pair rungs + endpoint spheres */}
      {rungs.map((r, i) => {
        const mid = new THREE.Vector3()
          .addVectors(r.p1, r.p2)
          .multiplyScalar(0.5);
        const dir = new THREE.Vector3().subVectors(r.p2, r.p1);
        const len = dir.length();
        dir.normalize();

        // Orient cylinder along the rung direction
        const quat = new THREE.Quaternion();
        quat.setFromUnitVectors(new THREE.Vector3(0, 1, 0), dir);

        return (
          <group key={i}>
            {/* Rung cylinder */}
            <mesh
              position={[mid.x, mid.y, mid.z]}
              quaternion={quat}
              material={rungMat}
            >
              <cylinderGeometry args={[0.03, 0.03, len, 6]} />
            </mesh>

            {/* Endpoint sphere — strand 1 side */}
            <mesh
              position={[r.p1.x, r.p1.y, r.p1.z]}
              material={sphereMat1}
            >
              <sphereGeometry args={[0.16, 16, 16]} />
            </mesh>

            {/* Endpoint sphere — strand 2 side */}
            <mesh
              position={[r.p2.x, r.p2.y, r.p2.z]}
              material={sphereMat2}
            >
              <sphereGeometry args={[0.16, 16, 16]} />
            </mesh>
          </group>
        );
      })}
    </group>
  );
}

/* ============================================================
   Floating particle field
   ============================================================ */
function Particles() {
  const ref = useRef();
  const count = 80;

  const positions = useMemo(() => {
    const arr = new Float32Array(count * 3);
    for (let i = 0; i < count; i++) {
      arr[i * 3] = (Math.random() - 0.5) * 10;
      arr[i * 3 + 1] = (Math.random() - 0.5) * 14;
      arr[i * 3 + 2] = (Math.random() - 0.5) * 6;
    }
    return arr;
  }, []);

  useFrame((_, delta) => {
    if (ref.current) {
      const pos = ref.current.geometry.attributes.position.array;
      for (let i = 0; i < count; i++) {
        pos[i * 3 + 1] += delta * 0.15;
        if (pos[i * 3 + 1] > 7) pos[i * 3 + 1] = -7;
      }
      ref.current.geometry.attributes.position.needsUpdate = true;
    }
  });

  return (
    <points ref={ref}>
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={count}
          array={positions}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial
        size={0.06}
        color={new THREE.Color(0.2, 0.6, 1)}
        transparent
        opacity={0.5}
        sizeAttenuation
      />
    </points>
  );
}

/* ============================================================
   Main exported component
   ============================================================ */
function DnaAnimation() {
  return (
    <div style={{ width: "100%", height: "100%", background: "#060612" }}>
      <Canvas
        camera={{ position: [0, 0, 7], fov: 50 }}
        gl={{ antialias: true, alpha: false }}
        dpr={[1, 2]}
      >
        <color attach="background" args={["#060612"]} />

        {/* Lighting */}
        <ambientLight intensity={0.15} />
        <pointLight
          position={[3, 4, 5]}
          intensity={1.5}
          color={new THREE.Color(0.3, 0.7, 1)}
        />
        <pointLight
          position={[-3, -2, 3]}
          intensity={1.0}
          color={new THREE.Color(0.5, 0.2, 0.9)}
        />
        <pointLight
          position={[0, 0, 4]}
          intensity={0.6}
          color={new THREE.Color(0.2, 0.5, 1)}
        />

        <DnaHelix />
        <Particles />

        {/* Bloom post-processing for the glow effect */}
        <EffectComposer>
          <Bloom
            intensity={1.2}
            luminanceThreshold={0.15}
            luminanceSmoothing={0.9}
            mipmapBlur
          />
        </EffectComposer>
      </Canvas>
    </div>
  );
}

export default DnaAnimation;
