import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";
import "./EmergencyCases.css";

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

const DEPARTMENTS = [
  "Emergency",
  "General_Medicine",
  "Cardiology",
  "Neurology",
  "Pulmonology",
  "Gastroenterology",
  "Orthopedics",
  "Pediatrics",
  "Nephrology",
  "Endocrinology",
];

const EMPTY_PATIENT = {
  full_name: "",
  age: "",
  gender: "",
  blood_group: "",
  allergies: "",
  past_surgeries: "",
  diabetes: false,
  hypertension: false,
  heart_disease: false,
  asthma: false,
  chronic_kidney_disease: false,
  previous_stroke: false,
  smoker: false,
  obese: false,
  previous_heart_attack: false,
  previous_hospitalization: false,
};

function EmergencyCases() {
  const [nurseName, setNurseName] = useState("");
  const navigate = useNavigate();

  // Patient states
  const [patientType, setPatientType] = useState("existing"); // "existing" | "new"
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [newPatient, setNewPatient] = useState({ ...EMPTY_PATIENT });
  const [saving, setSaving] = useState(false);

  // Confirmation states
  const [chosenDepartment, setChosenDepartment] = useState("");
  const [overrideMode, setOverrideMode] = useState(false);
  const [doctors, setDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [confirming, setConfirming] = useState(false);
  const [confirmed, setConfirmed] = useState(null);

  // Voice states
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [triageResult, setTriageResult] = useState(null);
  const [triageLoading, setTriageLoading] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [translating, setTranslating] = useState(false);

  useEffect(() => {
    fetch("http://localhost:8000/api/user-role/", {
      credentials: "include",
    })
      .then((res) => {
        if (!res.ok) {
          navigate("/");
          return null;
        }
        return res.json();
      })
      .then((roleData) => {
        if (!roleData) return;
        if (roleData.role !== "nurse") {
          navigate("/");
          return;
        }
        setNurseName(roleData.name || "");
      });
  }, [navigate]);

  const handleLogout = async () => {
    const csrfToken = getCookie("csrftoken");
    await fetch("http://localhost:8000/api/logout/", {
      method: "POST",
      credentials: "include",
      headers: { "X-CSRFToken": csrfToken },
    });
    navigate("/");
  };

  // ---------- Patient Search ----------
  useEffect(() => {
    if (patientType !== "existing" || searchQuery.trim().length < 2) {
      setSearchResults([]);
      return;
    }

    const timer = setTimeout(() => {
      fetch(
        `http://localhost:8000/api/patient/search/?q=${encodeURIComponent(searchQuery)}`,
        {
          credentials: "include",
        },
      )
        .then((res) => res.json())
        .then((data) => {
          if (Array.isArray(data)) setSearchResults(data);
        })
        .catch(() => {});
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery, patientType]);

  const selectPatient = (patient) => {
    setSelectedPatient(patient);
    setSearchQuery("");
    setSearchResults([]);
  };

  // ---------- New Patient ----------
  const handleNewPatientChange = (field, value) => {
    setNewPatient((prev) => ({ ...prev, [field]: value }));
  };

  const handleCreatePatient = async () => {
    if (!newPatient.full_name.trim() || !newPatient.age || !newPatient.gender) {
      alert("Name, age, and gender are required.");
      return;
    }

    setSaving(true);
    try {
      const csrfToken = getCookie("csrftoken");
      const res = await fetch("http://localhost:8000/api/patient/create/", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          "X-CSRFToken": csrfToken,
        },
        body: JSON.stringify(newPatient),
      });

      const result = await res.json();
      if (res.ok) {
        setSelectedPatient({ ...newPatient, id: result.id });
        setNewPatient({ ...EMPTY_PATIENT });
      } else {
        alert(result.error || "Failed to create patient.");
      }
    } catch {
      alert("Error connecting to server.");
    }
    setSaving(false);
  };

  // ---------- Voice Recording ----------
  const startListening = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      setMediaRecorder(recorder);

      const chunks = [];

      recorder.ondataavailable = (event) => {
        if (event.data.size > 0) chunks.push(event.data);
      };

      recorder.onstop = async () => {
        const audioBlob = new Blob(chunks, { type: "audio/webm" });
        const formData = new FormData();
        formData.append("audio", audioBlob, "recording.webm");

        setTranslating(true);
        try {
          const response = await fetch("http://localhost:8000/api/whisper/", {
            method: "POST",
            credentials: "include",
            body: formData,
          });
          const data = await response.json();
          if (data.translated_text) {
            setTranscript(data.translated_text);
          } else {
            alert("Transcription failed.");
          }
        } catch {
          alert("Error sending audio to server.");
        }
        setTranslating(false);
      };

      recorder.start();
      setIsListening(true);
    } catch {
      alert("Microphone access denied.");
    }
  };

  const stopListening = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      setIsListening(false);
    }
  };

  const handleTriageSubmit = async () => {
    if (!transcript.trim()) return;

    setTriageLoading(true);
    setTriageResult(null);

    try {
      const csrfToken = getCookie("csrftoken");
      const res = await fetch("http://localhost:8000/api/triage/", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          "X-CSRFToken": csrfToken,
        },
        body: JSON.stringify({ symptoms: transcript }),
      });
      const result = await res.json();
      setTriageResult(result);
    } catch {
      setTriageResult({ error: "Failed to reach server." });
    }
    setTriageLoading(false);
  };

  const clearVoice = () => {
    setTranscript("");
    setTriageResult(null);
    setChosenDepartment("");
    setOverrideMode(false);
    setDoctors([]);
    setSelectedDoctor(null);
    setConfirmed(null);
  };

  // ---------- Fetch doctors when department chosen ----------
  useEffect(() => {
    if (!chosenDepartment) {
      setDoctors([]);
      setSelectedDoctor(null);
      return;
    }

    fetch(
      `http://localhost:8000/api/doctors/?department=${encodeURIComponent(chosenDepartment)}`,
      { credentials: "include" },
    )
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setDoctors(data);
      })
      .catch(() => {});
  }, [chosenDepartment]);

  // When triage result comes in, set recommended department
  useEffect(() => {
    if (triageResult && !triageResult.error && triageResult.assigned_department) {
      setChosenDepartment(triageResult.assigned_department);
      setOverrideMode(false);
      setConfirmed(null);
    }
  }, [triageResult]);

  // ---------- Confirm Assignment ----------
  const handleConfirm = async () => {
    if (!selectedDoctor || !chosenDepartment || !selectedPatient) return;

    setConfirming(true);
    try {
      const csrfToken = getCookie("csrftoken");
      const res = await fetch("http://localhost:8000/api/emergency/confirm/", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          "X-CSRFToken": csrfToken,
        },
        body: JSON.stringify({
          patient_id: selectedPatient.id,
          doctor_id: selectedDoctor.id,
          department: chosenDepartment,
          symptoms: transcript,
        }),
      });
      const result = await res.json();
      if (res.ok) {
        setConfirmed(result);
      } else {
        alert(result.error || "Failed to confirm.");
      }
    } catch {
      alert("Error connecting to server.");
    }
    setConfirming(false);
  };

  // ---------- Render ----------
  return (
    <div className="nurse-page">
      <header className="nurse-header">
        <div className="nurse-header-left">
          <span>Apex Health</span>
        </div>
        <div className="nurse-header-right">
          <span className="nurse-name">
            Nurse: <strong>{nurseName}</strong>
          </span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <main className="nurse-main">
        {/* ===== STEP 1: Patient Selection ===== */}
        <h2 className="section-title">Patient</h2>

        {!selectedPatient ? (
          <>
            <div className="patient-toggle">
              <button
                className={`toggle-btn ${patientType === "existing" ? "active" : ""}`}
                onClick={() => {
                  setPatientType("existing");
                  setNewPatient({ ...EMPTY_PATIENT });
                }}
              >
                Existing Patient
              </button>
              <button
                className={`toggle-btn ${patientType === "new" ? "active" : ""}`}
                onClick={() => {
                  setPatientType("new");
                  setSearchQuery("");
                  setSearchResults([]);
                }}
              >
                New Patient
              </button>
            </div>

            {patientType === "existing" && (
              <div className="patient-search">
                <input
                  type="text"
                  placeholder="Search by patient name..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
                {searchResults.length > 0 && (
                  <div className="search-results">
                    {searchResults.map((p) => (
                      <div
                        key={p.id}
                        className="search-result-item"
                        onClick={() => selectPatient(p)}
                      >
                        <strong>{p.full_name}</strong>
                        <span>
                          {p.age} yrs Â· {p.gender}
                        </span>
                        {p.blood_group && <span> Â· {p.blood_group}</span>}
                      </div>
                    ))}
                  </div>
                )}
                {searchQuery.trim().length >= 2 &&
                  searchResults.length === 0 && (
                    <p className="no-results">
                      No patients found. Try a different name or add a new
                      patient.
                    </p>
                  )}
              </div>
            )}

            {patientType === "new" && (
              <div className="new-patient-form">
                <div className="form-row">
                  <div className="form-field">
                    <label>Full Name *</label>
                    <input
                      type="text"
                      value={newPatient.full_name}
                      onChange={(e) =>
                        handleNewPatientChange("full_name", e.target.value)
                      }
                    />
                  </div>
                  <div className="form-field">
                    <label>Age *</label>
                    <input
                      type="number"
                      value={newPatient.age}
                      onChange={(e) =>
                        handleNewPatientChange("age", e.target.value)
                      }
                    />
                  </div>
                  <div className="form-field">
                    <label>Gender *</label>
                    <select
                      value={newPatient.gender}
                      onChange={(e) =>
                        handleNewPatientChange("gender", e.target.value)
                      }
                    >
                      <option value="">Select</option>
                      <option value="Male">Male</option>
                      <option value="Female">Female</option>
                      <option value="Other">Other</option>
                    </select>
                  </div>
                </div>

                <div className="form-row">
                  <div className="form-field">
                    <label>Blood Group</label>
                    <select
                      value={newPatient.blood_group}
                      onChange={(e) =>
                        handleNewPatientChange("blood_group", e.target.value)
                      }
                    >
                      <option value="">Select</option>
                      <option value="A+">A+</option>
                      <option value="A-">A-</option>
                      <option value="B+">B+</option>
                      <option value="B-">B-</option>
                      <option value="O+">O+</option>
                      <option value="O-">O-</option>
                      <option value="AB+">AB+</option>
                      <option value="AB-">AB-</option>
                    </select>
                  </div>
                  <div className="form-field">
                    <label>Allergies</label>
                    <input
                      type="text"
                      value={newPatient.allergies}
                      onChange={(e) =>
                        handleNewPatientChange("allergies", e.target.value)
                      }
                      placeholder="e.g. Penicillin, Peanuts"
                    />
                  </div>
                  <div className="form-field">
                    <label>Past Surgeries</label>
                    <input
                      type="text"
                      value={newPatient.past_surgeries}
                      onChange={(e) =>
                        handleNewPatientChange("past_surgeries", e.target.value)
                      }
                      placeholder="e.g. Appendectomy"
                    />
                  </div>
                </div>

                <div className="medical-history">
                  <label className="history-heading">Medical History</label>
                  <div className="checkbox-grid">
                    {[
                      ["diabetes", "Diabetes"],
                      ["hypertension", "Hypertension"],
                      ["heart_disease", "Heart Disease"],
                      ["asthma", "Asthma"],
                      ["chronic_kidney_disease", "Chronic Kidney Disease"],
                      ["previous_stroke", "Previous Stroke"],
                      ["smoker", "Smoker"],
                      ["obese", "Obese"],
                      ["previous_heart_attack", "Previous Heart Attack"],
                      ["previous_hospitalization", "Previous Hospitalization"],
                    ].map(([field, label]) => (
                      <label key={field} className="checkbox-label">
                        <input
                          type="checkbox"
                          checked={newPatient[field]}
                          onChange={(e) =>
                            handleNewPatientChange(field, e.target.checked)
                          }
                        />
                        {label}
                      </label>
                    ))}
                  </div>
                </div>

                <button
                  className="voice-btn voice-btn-submit"
                  onClick={handleCreatePatient}
                  disabled={saving}
                >
                  {saving ? "Saving..." : "Register Patient"}
                </button>
              </div>
            )}
          </>
        ) : (
          <div className="selected-patient">
            <div className="selected-patient-info">
              <strong>{selectedPatient.full_name}</strong>
              <span>
                {selectedPatient.age} yrs Â· {selectedPatient.gender}
              </span>
              {selectedPatient.blood_group && (
                <span> Â· {selectedPatient.blood_group}</span>
              )}
            </div>
            <button
              className="voice-btn voice-btn-clear"
              onClick={() => setSelectedPatient(null)}
            >
              Change Patient
            </button>
          </div>
        )}

        {/* ===== STEP 2: Voice Triage (only after patient selected) ===== */}
        {selectedPatient && (
          <>
            <h2
              className="section-title"
              style={{ color: "#f87171", marginTop: 32 }}
            >
              Voice Triage
            </h2>

            <div className="voice-controls">
              {isListening ? (
                <button
                  className="voice-btn voice-btn-stop"
                  onClick={stopListening}
                >
                  Stop Recording
                </button>
              ) : (
                <button
                  className="voice-btn voice-btn-start"
                  onClick={startListening}
                >
                  Start Recording
                </button>
              )}

              {transcript && (
                <button
                  className="voice-btn voice-btn-clear"
                  onClick={clearVoice}
                >
                  Clear
                </button>
              )}
            </div>

            {isListening && <div className="voice-indicator">Listening...</div>}

            {translating && (
              <div className="voice-translating">
                <div className="translating-spinner"></div>
                <span>Translating audio...</span>
              </div>
            )}

            {transcript && (
              <div className="voice-transcript">
                <label>Translated Text:</label>
                <textarea
                  value={transcript}
                  onChange={(e) => setTranscript(e.target.value)}
                  rows={3}
                />
                <button
                  className="voice-btn voice-btn-submit"
                  onClick={handleTriageSubmit}
                  disabled={triageLoading}
                >
                  {triageLoading ? "Analyzing..." : "Analyze Symptoms"}
                </button>
              </div>
            )}

            {triageResult && triageResult.error && (
              <p className="voice-error">{triageResult.error}</p>
            )}

            {/* ===== STEP 3: Department Confirmation ===== */}
            {triageResult && !triageResult.error && !confirmed && (
              <div className="confirm-section">
                <h3 className="confirm-title">Assign Department</h3>

                <div className="confirm-recommendation">
                  <span className="confirm-label">AI Recommendation:</span>
                  <span className="confirm-dept-name">
                    {triageResult.assigned_department}
                  </span>
                  <span className="confirm-confidence">
                    {Math.round(triageResult.confidence * 100)}% confidence
                  </span>
                </div>

                {!overrideMode ? (
                  <div className="confirm-actions">
                    <button
                      className="confirm-btn confirm-btn-accept"
                      onClick={() => setChosenDepartment(triageResult.assigned_department)}
                      disabled={chosenDepartment === triageResult.assigned_department}
                    >
                      Accept Recommendation
                    </button>
                    <button
                      className="confirm-btn confirm-btn-override"
                      onClick={() => setOverrideMode(true)}
                    >
                      Override Department
                    </button>
                  </div>
                ) : (
                  <div className="override-section">
                    <label className="override-label">Select Department:</label>
                    <div className="dept-grid">
                      {DEPARTMENTS.map((dept) => (
                        <button
                          key={dept}
                          className={`dept-btn ${chosenDepartment === dept ? "active" : ""}`}
                          onClick={() => setChosenDepartment(dept)}
                        >
                          {dept.replace("_", " ")}
                        </button>
                      ))}
                    </div>
                    <button
                      className="confirm-btn confirm-btn-cancel"
                      onClick={() => {
                        setOverrideMode(false);
                        setChosenDepartment(triageResult.assigned_department);
                      }}
                    >
                      Cancel Override
                    </button>
                  </div>
                )}

                {/* Doctor selection */}
                {chosenDepartment && (
                  <div className="doctor-section">
                    <label className="doctor-label">
                      Assign Doctor ({chosenDepartment.replace("_", " ")}):
                    </label>
                    {doctors.length === 0 ? (
                      <p className="no-results">No doctors found for this department.</p>
                    ) : (
                      <div className="doctor-list">
                        {doctors.map((doc) => (
                          <div
                            key={doc.id}
                            className={`doctor-item ${selectedDoctor?.id === doc.id ? "active" : ""}`}
                            onClick={() => setSelectedDoctor(doc)}
                          >
                            <strong>{doc.name}</strong>
                            <span>{doc.department}</span>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                )}

                {/* Final confirm */}
                {selectedDoctor && (
                  <button
                    className="confirm-btn confirm-btn-final"
                    onClick={handleConfirm}
                    disabled={confirming}
                  >
                    {confirming
                      ? "Confirming..."
                      : `Confirm: ${selectedDoctor.name} â†’ ${chosenDepartment.replace("_", " ")}`}
                  </button>
                )}
              </div>
            )}

            {/* ===== Success ===== */}
            {confirmed && (
              <div className="confirm-success">
                <h3>Emergency Case Confirmed</h3>
                <p>
                  <strong>{confirmed.patient}</strong> assigned to{" "}
                  <strong>Dr. {confirmed.doctor}</strong> in{" "}
                  <strong>{confirmed.department.replace("_", " ")}</strong>
                </p>
                <button
                  className="confirm-btn confirm-btn-accept"
                  onClick={() => {
                    setSelectedPatient(null);
                    setTranscript("");
                    setTriageResult(null);
                    setChosenDepartment("");
                    setOverrideMode(false);
                    setDoctors([]);
                    setSelectedDoctor(null);
                    setConfirmed(null);
                  }}
                >
                  New Case
                </button>
              </div>
            )}
          </>
        )}
      </main>
    </div>
  );
}

export default EmergencyCases;
