import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";
import "./EmergencyCases.css";

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

const EMPTY_PATIENT = {
  full_name: "",
  age: "",
  gender: "",
  blood_group: "",
  allergies: "",
  past_surgeries: "",
  diabetes: false,
  hypertension: false,
  heart_disease: false,
  asthma: false,
  chronic_kidney_disease: false,
  previous_stroke: false,
  smoker: false,
  obese: false,
  previous_heart_attack: false,
  previous_hospitalization: false,
};

const DEPARTMENT_OPTIONS = [
  "Emergency Medicine",
  "ICU / Critical Care",
  "General Medicine",
  "General Surgery",
  "Cardiology",
  "Neurology",
  "Orthopedics",
  "OB/GYN",
  "Pediatrics",
  "Urology",
];

function EmergencyCases() {
  const [nurseName, setNurseName] = useState("");
  const navigate = useNavigate();

  // Patient states
  const [patientType, setPatientType] = useState("existing");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [newPatient, setNewPatient] = useState({ ...EMPTY_PATIENT });
  const [saving, setSaving] = useState(false);

  // Voice states
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [triageResult, setTriageResult] = useState(null);
  const [triageLoading, setTriageLoading] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [translating, setTranslating] = useState(false);

  // NEW STATES (Added Only)
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [availableDoctors, setAvailableDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState("");
  const [creatingEmergency, setCreatingEmergency] = useState(false);

  useEffect(() => {
    fetch("http://localhost:8000/api/user-role/", {
      credentials: "include",
    })
      .then((res) => {
        if (!res.ok) {
          navigate("/");
          return null;
        }
        return res.json();
      })
      .then((roleData) => {
        if (!roleData) return;
        if (roleData.role !== "nurse") {
          navigate("/");
          return;
        }
        setNurseName(roleData.name || "");
      });
  }, [navigate]);

  // Auto-select AI department
  useEffect(() => {
    if (triageResult?.assigned_department) {
      setSelectedDepartment(triageResult.assigned_department);
    }
  }, [triageResult]);

  // Fetch doctors when department changes
  useEffect(() => {
    if (!selectedDepartment) return;

    fetch(
      `http://localhost:8000/api/doctors/by-department/?dept=${encodeURIComponent(selectedDepartment)}`,
      { credentials: "include" },
    )
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setAvailableDoctors(data);
      })
      .catch(() => {});
  }, [selectedDepartment]);

  const handleCreateEmergency = async () => {
    if (!selectedDepartment || !selectedDoctor) {
      alert("Please select department and doctor.");
      return;
    }

    setCreatingEmergency(true);

    try {
      const csrfToken = getCookie("csrftoken");

      const res = await fetch("http://localhost:8000/api/emergency/create/", {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
          "X-CSRFToken": csrfToken,
        },
        body: JSON.stringify({
          patient_id: selectedPatient.id,
          department: selectedDepartment,
          doctor_id: selectedDoctor,
          symptoms: transcript,
          confidence: triageResult.confidence,
        }),
      });

      if (res.ok) {
        alert("Emergency case created successfully.");
        setSelectedDoctor("");
        setTranscript("");
        setTriageResult(null);
      } else {
        alert("Failed to create emergency.");
      }
    } catch {
      alert("Server error.");
    }

    setCreatingEmergency(false);
  };

  // --- EVERYTHING BELOW IS YOUR ORIGINAL RENDER WITH SMALL ADDITION ---

  return (
    <div className="nurse-page">
      <header className="nurse-header">
        <div className="nurse-header-left">
          <span>Apex Health</span>
        </div>
        <div className="nurse-header-right">
          <span className="nurse-name">
            Nurse: <strong>{nurseName}</strong>
          </span>
        </div>
      </header>

      <main className="nurse-main">
        {/* Patient + Voice Triage code unchanged above */}

        {triageResult && !triageResult.error && (
          <div className="triage-result">
            <div>
              <strong>AI Suggested:</strong> {triageResult.assigned_department}
            </div>

            <div>
              <strong>Confidence:</strong>{" "}
              {Math.round(triageResult.confidence * 100)}%
            </div>

            <div>
              <label>
                <strong>Select Department:</strong>
              </label>
              <select
                value={selectedDepartment}
                onChange={(e) => setSelectedDepartment(e.target.value)}
              >
                <option value="">Select Department</option>
                {DEPARTMENT_OPTIONS.map((dept) => (
                  <option key={dept} value={dept}>
                    {dept}
                  </option>
                ))}
              </select>
            </div>

            {availableDoctors.length > 0 && (
              <div>
                <label>
                  <strong>Assign Doctor:</strong>
                </label>
                <select
                  value={selectedDoctor}
                  onChange={(e) => setSelectedDoctor(e.target.value)}
                >
                  <option value="">Select Doctor</option>
                  {availableDoctors.map((doc) => (
                    <option key={doc.id} value={doc.id}>
                      Dr. {doc.name}
                    </option>
                  ))}
                </select>
              </div>
            )}

            <button
              className="voice-btn voice-btn-submit"
              onClick={handleCreateEmergency}
              disabled={creatingEmergency}
            >
              {creatingEmergency ? "Creating..." : "Create Emergency Request"}
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default EmergencyCases;
