import "./DnaAnimation.css";

const PAIR_COUNT = 32;

// Pre-compute particles outside component so they stay stable across re-renders
const PARTICLES = Array.from({ length: 20 }, (_, i) => {
  const seed = (i * 7 + 13) % 100;
  return {
    size: 2 + (seed / 100) * 5,
    left: 5 + ((seed * 3 + i * 17) % 90),
    top: (seed * 7 + i * 11) % 100,
    duration: 5 + (seed / 100) * 8,
    delay: (i * 3) % 6,
    isBlue: i % 2 === 0,
  };
});

function DnaAnimation() {
  const pairs = Array.from({ length: PAIR_COUNT }, (_, i) => {
    const rotation = (i * 360) / PAIR_COUNT;
    const top = (i / PAIR_COUNT) * 100;
    return (
      <div
        key={i}
        className="dna-pair"
        style={{
          top: `${top}%`,
          transform: `rotateY(${rotation}deg)`,
        }}
      >
        <div className="dna-pair-inner">
          <div className="dna-dot dna-dot-left" />
          <div className="dna-connector" />
          <div className="dna-dot dna-dot-right" />
        </div>
      </div>
    );
  });

  const particles = PARTICLES.map((p, i) => (
    <div
      key={`p-${i}`}
      className="dna-particle"
      style={{
        width: p.size,
        height: p.size,
        left: `${p.left}%`,
        top: `${p.top}%`,
        background: p.isBlue
          ? "rgba(0, 180, 216, 0.5)"
          : "rgba(123, 47, 247, 0.4)",
        boxShadow: p.isBlue
          ? "0 0 8px rgba(0, 180, 216, 0.3)"
          : "0 0 8px rgba(123, 47, 247, 0.3)",
        animationDuration: `${p.duration}s`,
        animationDelay: `${p.delay}s`,
      }}
    />
  ));

  return (
    <div className="dna-container">
      <div className="dna-helix">{pairs}</div>
      {particles}
    </div>
  );
}

export default DnaAnimation;
