import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";
import "./EmergencyCases.css";

const DEPARTMENT_OPTIONS = [
  "Emergency Medicine",
  "ICU / Critical Care",
  "General Medicine",
  "General Surgery",
  "Cardiology",
  "Neurology",
  "Orthopedics",
  "OB/GYN",
  "Pediatrics",
  "Urology",
];

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

function EmergencyCases() {
  const navigate = useNavigate();

  const [selectedPatient, setSelectedPatient] = useState(null);
  const [transcript, setTranscript] = useState("");
  const [triageResult, setTriageResult] = useState(null);
  const [triageLoading, setTriageLoading] = useState(false);

  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [availableDoctors, setAvailableDoctors] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState("");
  const [creatingEmergency, setCreatingEmergency] = useState(false);

  const [isListening, setIsListening] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);

  // ---------------- Voice Recording ----------------
  const startListening = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    setMediaRecorder(recorder);
    const chunks = [];

    recorder.ondataavailable = (e) => {
      if (e.data.size > 0) chunks.push(e.data);
    };

    recorder.onstop = async () => {
      const audioBlob = new Blob(chunks, { type: "audio/webm" });
      const formData = new FormData();
      formData.append("audio", audioBlob, "recording.webm");

      const response = await fetch("http://localhost:8000/api/whisper/", {
        method: "POST",
        credentials: "include",
        body: formData,
      });

      const data = await response.json();
      if (data.translated_text) {
        setTranscript(data.translated_text);
      }
    };

    recorder.start();
    setIsListening(true);
  };

  const stopListening = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      setIsListening(false);
    }
  };

  // ---------------- AI Triage ----------------
  const handleTriageSubmit = async () => {
    if (!transcript.trim()) return;

    setTriageLoading(true);

    const csrfToken = getCookie("csrftoken");

    const res = await fetch("http://localhost:8000/api/triage/", {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
        "X-CSRFToken": csrfToken,
      },
      body: JSON.stringify({ symptoms: transcript }),
    });

    const result = await res.json();
    setTriageResult(result);
    setTriageLoading(false);
  };

  // Auto-fill department after AI result
  useEffect(() => {
    if (triageResult?.assigned_department) {
      setSelectedDepartment(triageResult.assigned_department);
    }
  }, [triageResult]);

  // Fetch doctors when department changes
  useEffect(() => {
    if (!selectedDepartment) return;

    fetch(
      `http://localhost:8000/api/doctors/by-department/?dept=${encodeURIComponent(
        selectedDepartment,
      )}`,
      { credentials: "include" },
    )
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setAvailableDoctors(data);
      });
  }, [selectedDepartment]);

  // ---------------- Create Emergency ----------------
  const handleCreateEmergency = async () => {
    if (!selectedDepartment || !selectedDoctor) {
      alert("Select department and doctor.");
      return;
    }

    setCreatingEmergency(true);

    const csrfToken = getCookie("csrftoken");

    const res = await fetch("http://localhost:8000/api/emergency/create/", {
      method: "POST",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
        "X-CSRFToken": csrfToken,
      },
      body: JSON.stringify({
        patient_id: selectedPatient.id,
        department: selectedDepartment,
        doctor_id: selectedDoctor,
        symptoms: transcript,
        confidence: triageResult?.confidence || 0,
      }),
    });

    if (res.ok) {
      alert("Emergency case created successfully.");
      setTranscript("");
      setTriageResult(null);
      setSelectedDoctor("");
    } else {
      alert("Failed to create emergency case.");
    }

    setCreatingEmergency(false);
  };

  // ---------------- UI ----------------
  return (
    <div className="nurse-page">
      <main className="nurse-main">
        <h2>Voice Triage</h2>

        <div className="voice-controls">
          {isListening ? (
            <button onClick={stopListening}>Stop Recording</button>
          ) : (
            <button onClick={startListening}>Start Recording</button>
          )}
        </div>

        {transcript && (
          <div>
            <textarea
              value={transcript}
              onChange={(e) => setTranscript(e.target.value)}
              rows={3}
            />
            <button onClick={handleTriageSubmit} disabled={triageLoading}>
              {triageLoading ? "Analyzing..." : "Analyze Symptoms"}
            </button>
          </div>
        )}

        {triageResult && (
          <div className="triage-result">
            <div>
              <strong>AI Suggested:</strong> {triageResult.assigned_department}
            </div>

            <div>
              <strong>Confidence:</strong>{" "}
              {Math.round((triageResult.confidence || 0) * 100)}%
            </div>

            {/* Department Dropdown */}
            <div>
              <label>
                <strong>Select Department:</strong>
              </label>
              <select
                value={selectedDepartment}
                onChange={(e) => setSelectedDepartment(e.target.value)}
              >
                <option value="">Select Department</option>
                {DEPARTMENT_OPTIONS.map((dept) => (
                  <option key={dept} value={dept}>
                    {dept}
                  </option>
                ))}
              </select>
            </div>

            {/* Doctor Dropdown */}
            {availableDoctors.length > 0 && (
              <div>
                <label>
                  <strong>Assign Doctor:</strong>
                </label>
                <select
                  value={selectedDoctor}
                  onChange={(e) => setSelectedDoctor(e.target.value)}
                >
                  <option value="">Select Doctor</option>
                  {availableDoctors.map((doc) => (
                    <option key={doc.id} value={doc.id}>
                      Dr. {doc.name}
                    </option>
                  ))}
                </select>
              </div>
            )}

            <button
              onClick={handleCreateEmergency}
              disabled={creatingEmergency}
            >
              {creatingEmergency ? "Creating..." : "Create Emergency Request"}
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default EmergencyCases;
