import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./NurseDashboard.css";

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== "") {
    const cookies = document.cookie.split(";");
    for (let cookie of cookies) {
      cookie = cookie.trim();
      if (cookie.startsWith(name + "=")) {
        cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
        break;
      }
    }
  }
  return cookieValue;
}

function formatDate(dateStr) {
  if (!dateStr) return "";
  const d = new Date(dateStr);
  return d.toLocaleDateString("en-IN", {
    day: "numeric",
    month: "short",
    year: "numeric",
    hour: "2-digit",
    minute: "2-digit",
  });
}

function Inpatients() {
  const [data, setData] = useState([]);
  const [nurseName, setNurseName] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://192.168.18.198:8000/api/user-role/", {
      credentials: "include",
    })
      .then(res => {
        if (!res.ok) { navigate("/"); return null; }
        return res.json();
      })
      .then(roleData => {
        if (!roleData) return;
        if (roleData.role !== "nurse") { navigate("/"); return; }
        setNurseName(roleData.name || "");
      });
  }, [navigate]);

  useEffect(() => {
    fetch("http://192.168.18.198:8000/api/nurse-dashboard/", {
      credentials: "include",
    })
      .then(res => res.json())
      .then(result => {
        if (Array.isArray(result)) {
          const inpatients = result.filter(item => {
            const risk = (item.predicted_risk || "").toLowerCase();
            return risk === "low" || risk === "moderate" || risk === "medium";
          });
          setData(inpatients);
        }
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleLogout = async () => {
    const csrfToken = getCookie("csrftoken");
    await fetch("http://192.168.18.198:8000/api/logout/", {
      method: "POST",
      credentials: "include",
      headers: { "X-CSRFToken": csrfToken },
    });
    navigate("/");
  };

  if (loading) return <div className="nurse-loading">Loading...</div>;

  return (
    <div className="nurse-page">
      <header className="nurse-header">
        <div className="nurse-header-left">
          <svg viewBox="0 0 48 48" xmlns="http://www.w3.org/2000/svg">
            <rect x="18" y="4" width="12" height="30" rx="3" fill="white" />
            <rect x="6" y="12" width="36" height="12" rx="3" fill="white" />
            <ellipse cx="36" cy="38" rx="6" ry="3" fill="none" stroke="white" strokeWidth="1.5" />
            <ellipse cx="36" cy="42" rx="6" ry="3" fill="none" stroke="white" strokeWidth="1.5" />
          </svg>
          <span>Apex Health</span>
        </div>
        <div className="nurse-header-right">
          <span className="nurse-name">
            Nurse: <strong>{nurseName}</strong>
          </span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <main className="nurse-main">
        <button
          className="back-btn"
          onClick={() => navigate("/nurse")}
        >
          Back to Dashboard
        </button>

        <h2 className="section-title" style={{ color: "#818cf8" }}>
          Inpatients
        </h2>

        {data.length === 0 ? (
          <p className="no-data">No inpatient cases found.</p>
        ) : (
          <div className="triage-grid">
            {data.map(item => (
              <div key={item.id} className="triage-card" style={{ borderColor: "rgba(67, 97, 238, 0.2)" }}>
                <div className="triage-card-header">
                  <div>
                    <div className="patient-name">
                      {item.patient_name || `Patient #${item.id}`}
                    </div>
                    <div className="patient-meta">
                      {item.patient_age && `${item.patient_age} yrs`}
                      {item.patient_gender && ` · ${item.patient_gender}`}
                    </div>
                  </div>
                  <span className={`risk-badge ${
                    (item.predicted_risk || "").toLowerCase() === "low" ? "risk-low" : "risk-moderate"
                  }`}>
                    {item.predicted_risk || "Pending"}
                  </span>
                </div>

                <div className="vitals-grid">
                  <div className="vital-item">
                    <div className="vital-label">BP</div>
                    <div className="vital-value">{item.systolic_bp || "—"}</div>
                  </div>
                  <div className="vital-item">
                    <div className="vital-label">HR</div>
                    <div className="vital-value">{item.heart_rate || "—"}</div>
                  </div>
                  <div className="vital-item">
                    <div className="vital-label">Temp</div>
                    <div className="vital-value">
                      {item.temperature ? `${item.temperature}°` : "—"}
                    </div>
                  </div>
                  <div className="vital-item">
                    <div className="vital-label">O2</div>
                    <div className="vital-value">
                      {item.oxygen ? `${item.oxygen}%` : "—"}
                    </div>
                  </div>
                </div>

                <div className="triage-card-footer">
                  <span className="department">
                    {item.recommended_department || "Unassigned"}
                  </span>
                  <span className="timestamp">
                    {formatDate(item.created_at)}
                  </span>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

export default Inpatients;
