import "./DnaAnimation.css";

const PAIR_COUNT = 36;

// Seeded pseudo-random so particles don't re-randomize on re-render
const PARTICLES = Array.from({ length: 30 }, (_, i) => {
  const seed = (i * 7 + 13) % 100;
  const size = 1.5 + (seed / 100) * 5;
  const left = 3 + ((seed * 3 + i * 17) % 94);
  const top = (seed * 7 + i * 11) % 100;
  const duration = 5 + (seed / 100) * 9;
  const delay = ((i * 3) % 8);
  const isBlue = i % 3 !== 0;
  const isDrift = i % 2 === 0;
  return { size, left, top, duration, delay, isBlue, isDrift };
});

const RAYS = [
  { w: 180, h: 80, left: "15%", top: "20%", color: "rgba(0,180,216,0.12)", dur: 5, delay: 0 },
  { w: 120, h: 60, left: "60%", top: "65%", color: "rgba(139,92,246,0.1)", dur: 6, delay: 1.5 },
  { w: 200, h: 50, left: "30%", top: "80%", color: "rgba(67,97,238,0.08)", dur: 7, delay: 3 },
];

function DnaAnimation() {
  const pairs = Array.from({ length: PAIR_COUNT }, (_, i) => {
    const rotation = (i * 360) / PAIR_COUNT;
    const top = (i / PAIR_COUNT) * 100;
    return (
      <div
        key={i}
        className="dna-pair"
        style={{
          top: `${top}%`,
          transform: `rotateY(${rotation}deg)`,
        }}
      >
        <div className="dna-pair-inner">
          <div className="dna-sphere dna-sphere-left" />
          <div className="dna-connector" />
          <div className="dna-sphere dna-sphere-right" />
        </div>
      </div>
    );
  });

  const particles = PARTICLES.map((p, i) => (
    <div
      key={`p-${i}`}
      className={`dna-particle ${p.isDrift ? "dna-particle-drift" : "dna-particle-twinkle"}`}
      style={{
        width: p.size,
        height: p.size,
        left: `${p.left}%`,
        top: `${p.top}%`,
        background: p.isBlue
          ? "radial-gradient(circle, rgba(0,180,216,0.9), rgba(0,180,216,0) 70%)"
          : "radial-gradient(circle, rgba(139,92,246,0.9), rgba(139,92,246,0) 70%)",
        boxShadow: p.isBlue
          ? "0 0 6px 2px rgba(0,180,216,0.4)"
          : "0 0 6px 2px rgba(139,92,246,0.4)",
        animationDuration: `${p.duration}s`,
        animationDelay: `${p.delay}s`,
      }}
    />
  ));

  const rays = RAYS.map((r, i) => (
    <div
      key={`r-${i}`}
      className="dna-ray"
      style={{
        width: r.w,
        height: r.h,
        left: r.left,
        top: r.top,
        background: r.color,
        animationDuration: `${r.dur}s`,
        animationDelay: `${r.delay}s`,
      }}
    />
  ));

  return (
    <div className="dna-container">
      {rays}
      <div className="dna-helix">{pairs}</div>
      {particles}
    </div>
  );
}

export default DnaAnimation;
